import React from "react";
import { connect } from "react-redux";
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import Button from 'antd/lib/button';
import Table  from 'antd/lib/table';
import { Form, Input, Tooltip, Icon} from "antd";////////////////////////
//import { Divider} from 'antd/lib/divider';
//import { Tag } from 'antd/lib/tag';
import './App.css';
import * as tabledemo from './tabledemo';
import RegistrationForm from './formdemo';
import * as actions from "./redux/actions";


class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = { input: "" }; 
    }

    handleInput = e => {
      this.setState({ input: e.target.value });
      this.props.getResult(e.target.value);
    };

    render() {
      const { input } = this.state;
      const { matchedData } = this.props;
      const WrappedRegistrationForm = Form.create({ name: "register" })(
        RegistrationForm
      );

      const Home = () => (
        <div>
          <h2>Home</h2>
        </div>
      );

      /* About component */
      const Creat = () => (
        <div>
          <h2>Creat</h2>
          <WrappedRegistrationForm />
        </div>
      );

      /* Users component */
      const Edit = () => (
        <div>
          <h2>Edit</h2>
          <WrappedRegistrationForm />
        </div>
      );

    return (
      <div>
      <BrowserRouter>
            <Switch>
              <Route exact = {true} path="/home" component={Home} />
              <Route path="/creat" component={Creat} />
              <Route path="/edit" component={Edit} />
            </Switch>
      </BrowserRouter>

              <input value={input} onChange={this.handleInput}/>
              {matchedData && matchedData.length > 0 && 
                <ul>
                  {matchedData.map((data, index) => {
                    return <li key={index}>{data}</li>
                  })}
                </ul>
              }
            <Button type="primary">Button</Button>
            <Table columns={tabledemo.columns} dataSource={tabledemo.data} ></Table>

        </div>


    );
  }
}

const mapStateToProps = state => {
  return {
    matchedData: state.data
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getResult: searchText => dispatch(actions.getMatchedData(searchText))
  };
};


export default connect(mapStateToProps, mapDispatchToProps)(App);
