import React from 'react';
import {Link} from 'react-router-dom';
import Button from 'antd/lib/button';
import Table  from 'antd/lib/table';
import {connect} from 'react-redux';
import { getList, delUser, getMatchedData} from "../../redux/actions";
import Highlighter from 'react-highlight-words';

import Divider from 'antd/lib/divider';
import Popconfirm from 'antd/lib/popconfirm';
import Input from 'antd/lib/input';
import Icon from 'antd/lib/icon';

class Home extends React.Component {

    constructor(props) {
        super(props);
        this.columns = [
        {
            title: 'Sex',
            dataIndex: 'sex',
            key: 'sex',
            ...this.getColumnSearchProps('sex'),
            sorter: (a, b) => a.age - b.age,
            //render: text => <a href="javascript:;">{text}</a>,
        },
        {
            title: 'Age',
            dataIndex: 'age',
            key: 'age',
            ...this.getColumnSearchProps('age'),
        },
        {
            title: 'First Name',
            dataIndex: 'firstName',
            key: 'firstName',
            ...this.getColumnSearchProps('firstName'),
        },
        {
            title: 'Last Name',
            dataIndex: 'lastName',
            key: 'lastName',
            ...this.getColumnSearchProps('lastName'),
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
            <span>
                <a href="javascript:;">Edit </a>{/*</span>{record.name}</a>*/}
                <Divider type="vertical" />
                <Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record._id)}>
                <a href="javascript:;">Delete</a>
                </Popconfirm>
            </span>
            ),
        },
        ];
        this.state = { input: "" }; 
    };

    getColumnSearchProps = dataIndex => ({
        //////////////////////////////////////////
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.input]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });



    handleInput = e => {
        this.setState({ input: e.target.value });
        //console.log('input', this.state.input)
    };
    handleSearch = () => {
        console.log(this.props.input)
        this.props.getSearchResult(this.state.input);
        //this.setState({ input: '' });
    }
    handleAdd = () => {
        const { count, dataSource } = this.state;
        const newData = {
          key: count,
          name: `Edward King ${count}`,
          age: 32,
          address: `London, Park Lane no. ${count}`,
        };
        this.setState({
          dataSource: [...dataSource, newData],
          count: count + 1,
        });
    };
    handleDelete = user_id => {
        console.log('user_id', user_id);
        //var key = user_id;
        //const dataSource = this.props.userList.data;
        //this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
        //console.log('dataSource', dataSource);
        this.props.deleteUser(user_id);
      };

    render(){
        const { input } = this.state;
        var userList = [];
        //const { userList, matchedData } = this.props;
        if (this.props.matchedData!== [])
        {
            userList = this.props.matchedData;
        }
        else
        {
            userList = this.props.userList;
        }
            return (
            <div>
            <h2>Home</h2>
                <input style = {{margin: 10}} value={input} onChange={this.handleInput}/>
                    {/*     {matchedData && matchedData.length > 0 && 
                        <ul>
                        {matchedData.map((data, index) => {
                            return <li key={index}>{data}</li>
                        })}
                        </ul>
                    }     */}
                <Button style = {{margin: 10}} type="primary" onClick = {this.handleSearch} >Search</Button>

                <Link style = {{margin: 10}} to="/creat">Creat new user</Link>
                
                <Table columns={this.columns} dataSource = {userList.data} ></Table>{/*tabledemo.data} ></Table>*/}
            </div>
        );
    }   
}

const mapStateToProps = state => {
    return {
      userList: state.list,
      deleteUser: state.deleteUser,
      matchedData: state.search
    };
  };
const mapDispatchToProps = dispatch => {
    return {
        getUserList: () => {
            dispatch(getList());
        },
        deleteUser: (id) => {
            dispatch(delUser(id));
        },
        getSearchResult: (searchText) => {
            dispatch(getMatchedData(searchText))
        }
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
