import React from 'react';
import Button from 'antd/lib/button';
import Table  from 'antd/lib/table';
import {connect} from 'react-redux';

//import * as tabledemo from '../tabledemo';
//import React from 'react';
import Divider from 'antd/lib/divider';
import Popconfirm from 'antd/lib/popconfirm';
const columns = [
  {
    title: 'Sex',
    dataIndex: 'sex',
    key: 'sex',
    sorter: (a, b) => a.age - b.age,
    render: text => <a href="javascript:;">{text}</a>,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'firstName',
    dataIndex: 'firstName',
    key: 'firstName',
  },
  {
    title: 'lastName',
    dataIndex: 'lastName',
    key: 'lastName',
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <span>
        <a href="javascript:;">Edit </a>{/*</span>{record.name}</a>*/}
        <Divider type="vertical" />
        <Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record.key)}>
          <a href="javascript:;">Delete</a>
        </Popconfirm>
      </span>
    ),
  },
];

class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = { input: "" }; 
        console.log(this.props.userList)
      }
    handleInput = e => {
        this.setState({ input: e.target.value });
        this.props.getResult(e.target.value);
    };
    handleDelete = key => {
        const dataSource = [...this.state.dataSource];
        this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
      };
    render(){
        const { input } = this.state;
        //const { userList, matchedData } = this.props;
        const userList = this.props.userList;
        return (
            <div>
            <h2>Home</h2>
                <input style = {{margin: 10}} value={input} onChange={this.handleInput}/>
                    {/*     {matchedData && matchedData.length > 0 && 
                        <ul>
                        {matchedData.map((data, index) => {
                            return <li key={index}>{data}</li>
                        })}
                        </ul>
                    }     */}
                <Button style = {{margin: 10}} type="primary">Search</Button>
                
                <Table columns={columns} dataSource = {userList.data} ></Table>{/*tabledemo.data} ></Table>*/}
            </div>
        );
    }   
}

const mapStateToProps = state => {
    return {
      userList: state.list,
    };
  };
  

  export default connect(mapStateToProps, )(Home);
