import React from "react";
import { connect } from "react-redux";
import {BrowserRouter, Route, Switch} from 'react-router-dom';

import { Form, Input, Tooltip, Icon} from "antd";////////////////////////
//import { Divider} from 'antd/lib/divider';
//import { Tag } from 'antd/lib/tag';
import './App.css';
import Home from './containers/Home';
import RegistrationForm from './formdemo';
import * as actions from "./redux/actions";


class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = { input: "" }; 
    }

    handleInput = e => {
      this.setState({ input: e.target.value });
      this.props.getResult(e.target.value);
    };

    render() {
      const { input } = this.state;
      const { matchedData } = this.props;
      const WrappedRegistrationForm = Form.create({ name: "register" })(
        RegistrationForm
      );



      const Creat = () => (
        <div>
          <h2>Creat</h2>
          <WrappedRegistrationForm />
        </div>
      );
      const Edit = () => (
        <div>
          <h2>Edit</h2>
          <WrappedRegistrationForm />
        </div>
      );

    return (
      <div>
      <BrowserRouter>
            <Switch>
              <Route exact = {true} path="/home" component={Home} />
              <Route exact = {true} path="/creat" component={Creat} />
              <Route exact = {true} path="/edit" component={Edit} />
            </Switch>
      </BrowserRouter>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    matchedData: state.data
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getResult: searchText => dispatch(actions.getMatchedData(searchText))
  };
};


export default connect(mapStateToProps, mapDispatchToProps)(App);
